// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

/**
 * @title MockOracle
 * @author TheBlocks Team - TriHacker Tournament 2025
 * @notice Mock Chainlink-compatible oracle for testing
 * @dev Implements AggregatorV3Interface for test deployments
 */
contract MockOracle {
    int256 private _price;
    uint8 private _decimals;
    string private _description;
    uint256 private _version;
    uint80 private _roundId;
    uint256 private _updatedAt;

    // Events for testing
    event PriceUpdated(int256 oldPrice, int256 newPrice, uint256 timestamp);
    event RoundUpdated(uint80 oldRound, uint80 newRound);

    /**
     * @notice Constructor
     * @param price Initial price (without decimals scaling - e.g., 100 for $1.00 with 8 decimals)
     * @param decimals_ Number of decimals for the price
     * @param description_ Description of the price feed (e.g., "ETH/USD")
     */
    constructor(int256 price, uint8 decimals_, string memory description_) {
        _price = price;
        _decimals = decimals_;
        _description = description_;
        _version = 4; // Chainlink V3 compatibility
        _roundId = 1;
        _updatedAt = block.timestamp;
    }

    // ==================== AggregatorV3Interface Implementation ====================

    /**
     * @notice Get the number of decimals
     */
    function decimals() external view returns (uint8) {
        return _decimals;
    }

    /**
     * @notice Get the description
     */
    function description() external view returns (string memory) {
        return _description;
    }

    /**
     * @notice Get the version
     */
    function version() external view returns (uint256) {
        return _version;
    }

    /**
     * @notice Get round data by round ID
     * @dev For mocking, all rounds return the same data
     */
    function getRoundData(uint80 roundId_) external view returns (
        uint80 roundId,
        int256 answer,
        uint256 startedAt,
        uint256 updatedAt,
        uint80 answeredInRound
    ) {
        return (
            roundId_,
            _price,
            _updatedAt,
            _updatedAt,
            roundId_
        );
    }

    /**
     * @notice Get the latest round data
     * @dev This is the main function used by SettlementOracle
     */
    function latestRoundData() external view returns (
        uint80 roundId,
        int256 answer,
        uint256 startedAt,
        uint256 updatedAt,
        uint80 answeredInRound
    ) {
        return (
            _roundId,
            _price,
            _updatedAt,
            _updatedAt,
            _roundId
        );
    }

    // ==================== Mock Control Functions ====================

    /**
     * @notice Update the mock price
     * @param newPrice New price to set
     */
    function setPrice(int256 newPrice) external {
        int256 oldPrice = _price;
        _price = newPrice;
        _updatedAt = block.timestamp;
        _roundId++;
        emit PriceUpdated(oldPrice, newPrice, block.timestamp);
    }

    /**
     * @notice Update multiple parameters at once
     * @param newPrice New price
     * @param newTimestamp New timestamp (for staleness testing)
     */
    function updateRound(int256 newPrice, uint256 newTimestamp) external {
        int256 oldPrice = _price;
        uint80 oldRound = _roundId;
        
        _price = newPrice;
        _updatedAt = newTimestamp;
        _roundId++;
        
        emit PriceUpdated(oldPrice, newPrice, newTimestamp);
        emit RoundUpdated(oldRound, _roundId);
    }

    /**
     * @notice Set a stale timestamp (for testing staleness checks)
     * @param staleTimestamp The stale timestamp to set
     */
    function setStaleTimestamp(uint256 staleTimestamp) external {
        _updatedAt = staleTimestamp;
    }

    /**
     * @notice Get current price (helper for tests)
     */
    function getPrice() external view returns (int256) {
        return _price;
    }

    /**
     * @notice Get current round ID (helper for tests)
     */
    function getRoundId() external view returns (uint80) {
        return _roundId;
    }

    /**
     * @notice Get last update timestamp (helper for tests)
     */
    function getUpdatedAt() external view returns (uint256) {
        return _updatedAt;
    }

    /**
     * @notice Simulate a price crash (for circuit breaker testing)
     * @param crashPercentage Percentage to crash (e.g., 50 = 50% crash)
     */
    function simulateCrash(uint256 crashPercentage) external {
        require(crashPercentage <= 100, "Invalid percentage");
        int256 oldPrice = _price;
        _price = _price * int256(100 - crashPercentage) / 100;
        _updatedAt = block.timestamp;
        _roundId++;
        emit PriceUpdated(oldPrice, _price, block.timestamp);
    }

    /**
     * @notice Simulate a price spike (for manipulation testing)
     * @param spikePercentage Percentage to spike (e.g., 50 = 50% spike)
     */
    function simulateSpike(uint256 spikePercentage) external {
        int256 oldPrice = _price;
        _price = _price * int256(100 + spikePercentage) / 100;
        _updatedAt = block.timestamp;
        _roundId++;
        emit PriceUpdated(oldPrice, _price, block.timestamp);
    }
}

contracts/OracleAggregator.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

import "@openzeppelin/contracts/access/AccessControl.sol";
import "@openzeppelin/contracts/utils/ReentrancyGuard.sol";

/**
 * @title OracleAggregator
 * @notice Multi-source FX rate aggregation with TWAP and circuit breakers
 * @dev Aggregates rates from multiple oracles for institutional-grade accuracy
 * 
 * KEY FEATURES:
 * - Multi-oracle price feeds with weighted averages
 * - Time-weighted average price (TWAP) calculation
 * - Circuit breakers for extreme volatility
 * - Staleness detection and fallback
 * - Rate caching for gas optimization
 */
contract OracleAggregator is AccessControl, ReentrancyGuard {
    bytes32 public constant ORACLE_ADMIN = keccak256("ORACLE_ADMIN");
    bytes32 public constant PRICE_UPDATER = keccak256("PRICE_UPDATER");

    struct PriceData {
        uint256 rate;               // Rate scaled to 18 decimals
        uint256 timestamp;          // Last update time
        uint256 confidence;         // Confidence level 0-100
        bool isActive;
    }

    struct OracleSource {
        address oracle;
        string name;
        uint256 weight;             // Weight in aggregation (sum = 10000)
        bool isActive;
        uint256 lastUpdate;
        uint256 totalUpdates;
    }

    struct CurrencyPair {
        string base;                // e.g., "USD"
        string quote;               // e.g., "EUR"
        bytes32 pairId;
        bool isActive;
        
        // Current rate
        uint256 currentRate;
        uint256 lastUpdate;
        
        // TWAP data
        uint256[] rateHistory;
        uint256[] timestamps;
        uint256 twapPeriod;         // TWAP calculation period
        
        // Circuit breaker
        uint256 maxDeviation;       // Max allowed deviation in bps
        bool circuitBreakerTripped;
    }

    struct RateResponse {
        uint256 spotRate;
        uint256 twapRate;
        uint256 confidence;
        uint256 timestamp;
        bool isStale;
        bool circuitBreakerActive;
    }

    // State
    mapping(bytes32 => CurrencyPair) public pairs;
    mapping(bytes32 => mapping(address => PriceData)) public oraclePrices;
    mapping(address => OracleSource) public oracleSources;
    
    bytes32[] public activePairs;
    address[] public activeOracles;
    
    // Settings
    uint256 public stalenessThreshold = 1 hours;
    uint256 public defaultTwapPeriod = 15 minutes;
    uint256 public defaultMaxDeviation = 500; // 5%
    uint256 public minOracleCount = 2;

    // Statistics
    uint256 public totalPriceUpdates;
    uint256 public totalQueries;
    uint256 public circuitBreakerTriggers;

    // Events
    event PriceUpdated(
        bytes32 indexed pairId,
        address indexed oracle,
        uint256 rate,
        uint256 timestamp
    );
    
    event PairAdded(
        bytes32 indexed pairId,
        string base,
        string quote
    );
    
    event OracleAdded(
        address indexed oracle,
        string name,
        uint256 weight
    );
    
    event CircuitBreakerTripped(
        bytes32 indexed pairId,
        uint256 expectedRate,
        uint256 actualRate,
        uint256 deviation
    );
    
    event CircuitBreakerReset(bytes32 indexed pairId);

    constructor() {
        _grantRole(DEFAULT_ADMIN_ROLE, msg.sender);
        _grantRole(ORACLE_ADMIN, msg.sender);
        _grantRole(PRICE_UPDATER, msg.sender);
        
        // Initialize common FX pairs
        _addPair("USD", "EUR");
        _addPair("USD", "GBP");
        _addPair("USD", "JPY");
        _addPair("EUR", "GBP");
        _addPair("USD", "USDC");
        _addPair("USD", "USDT");
        _addPair("ETH", "USD");
    }

    // ═══════════════════════════════════════════════════════════════════════════
    //                         PAIR MANAGEMENT
    // ═══════════════════════════════════════════════════════════════════════════

    function addPair(
        string calldata base,
        string calldata quote
    ) external onlyRole(ORACLE_ADMIN) returns (bytes32 pairId) {
        return _addPair(base, quote);
    }

    function _addPair(
        string memory base,
        string memory quote
    ) internal returns (bytes32 pairId) {
        pairId = keccak256(abi.encodePacked(base, "/", quote));
        require(!pairs[pairId].isActive, "Pair exists");
        
        CurrencyPair storage pair = pairs[pairId];
        pair.base = base;
        pair.quote = quote;
        pair.pairId = pairId;
        pair.isActive = true;
        pair.twapPeriod = defaultTwapPeriod;
        pair.maxDeviation = defaultMaxDeviation;
        
        activePairs.push(pairId);
        
        emit PairAdded(pairId, base, quote);
        
        return pairId;
    }

    // ═══════════════════════════════════════════════════════════════════════════
    //                         ORACLE MANAGEMENT
    // ═══════════════════════════════════════════════════════════════════════════

    function addOracle(
        address oracle,
        string calldata name,
        uint256 weight
    ) external onlyRole(ORACLE_ADMIN) {
        require(oracle != address(0), "Invalid oracle");
        require(!oracleSources[oracle].isActive, "Oracle exists");
        
        oracleSources[oracle] = OracleSource({
            oracle: oracle,
            name: name,
            weight: weight,
            isActive: true,
            lastUpdate: 0,
            totalUpdates: 0
        });
        
        activeOracles.push(oracle);
        
        emit OracleAdded(oracle, name, weight);
    }

    function updateOracleWeight(
        address oracle,
        uint256 weight
    ) external onlyRole(ORACLE_ADMIN) {
        require(oracleSources[oracle].isActive, "Oracle not found");
        oracleSources[oracle].weight = weight;
    }

    function deactivateOracle(address oracle) external onlyRole(ORACLE_ADMIN) {
        oracleSources[oracle].isActive = false;
    }

    // ═══════════════════════════════════════════════════════════════════════════
    //                         PRICE UPDATES
    // ═══════════════════════════════════════════════════════════════════════════

    /**
     * @notice Update price for a currency pair
     */
    function updatePrice(
        bytes32 pairId,
        uint256 rate,
        uint256 confidence
    ) external onlyRole(PRICE_UPDATER) {
        require(pairs[pairId].isActive, "Pair not active");
        require(rate > 0, "Invalid rate");
        require(confidence <= 100, "Invalid confidence");
        
        CurrencyPair storage pair = pairs[pairId];
        
        // Circuit breaker check
        if (pair.currentRate > 0) {
            uint256 deviation = _calculateDeviation(pair.currentRate, rate);
            if (deviation > pair.maxDeviation) {
                pair.circuitBreakerTripped = true;
                circuitBreakerTriggers++;
                emit CircuitBreakerTripped(pairId, pair.currentRate, rate, deviation);
                return;
            }
        }
        
        // Update oracle-specific price
        oraclePrices[pairId][msg.sender] = PriceData({
            rate: rate,
            timestamp: block.timestamp,
            confidence: confidence,
            isActive: true
        });
        
        oracleSources[msg.sender].lastUpdate = block.timestamp;
        oracleSources[msg.sender].totalUpdates++;
        
        // Calculate aggregated rate
        uint256 aggregatedRate = _calculateAggregatedRate(pairId);
        
        // Update pair data
        pair.currentRate = aggregatedRate;
        pair.lastUpdate = block.timestamp;
        
        // Add to history for TWAP
        pair.rateHistory.push(aggregatedRate);
        pair.timestamps.push(block.timestamp);
        
        // Trim history to keep only relevant period
        _trimHistory(pair);
        
        totalPriceUpdates++;
        
        emit PriceUpdated(pairId, msg.sender, rate, block.timestamp);
    }

    /**
     * @notice Batch update multiple prices
     */
    function batchUpdatePrices(
        bytes32[] calldata pairIds,
        uint256[] calldata rates,
        uint256[] calldata confidences
    ) external onlyRole(PRICE_UPDATER) {
        require(pairIds.length == rates.length, "Length mismatch");
        require(pairIds.length == confidences.length, "Length mismatch");
        
        for (uint256 i = 0; i < pairIds.length; i++) {
            if (pairs[pairIds[i]].isActive && rates[i] > 0) {
                oraclePrices[pairIds[i]][msg.sender] = PriceData({
                    rate: rates[i],
                    timestamp: block.timestamp,
                    confidence: confidences[i],
                    isActive: true
                });
                
                pairs[pairIds[i]].currentRate = rates[i];
                pairs[pairIds[i]].lastUpdate = block.timestamp;
            }
        }
        
        totalPriceUpdates += pairIds.length;
    }

    function _calculateAggregatedRate(bytes32 pairId) internal view returns (uint256) {
        uint256 weightedSum = 0;
        uint256 totalWeight = 0;
        uint256 validOracles = 0;
        
        for (uint256 i = 0; i < activeOracles.length; i++) {
            address oracle = activeOracles[i];
            if (!oracleSources[oracle].isActive) continue;
            
            PriceData storage price = oraclePrices[pairId][oracle];
            if (!price.isActive) continue;
            if (block.timestamp - price.timestamp > stalenessThreshold) continue;
            
            uint256 weight = oracleSources[oracle].weight;
            weightedSum += price.rate * weight;
            totalWeight += weight;
            validOracles++;
        }
        
        if (totalWeight == 0 || validOracles < minOracleCount) {
            return pairs[pairId].currentRate; // Fall back to last known rate
        }
        
        return weightedSum / totalWeight;
    }

    function _calculateDeviation(uint256 oldRate, uint256 newRate) internal pure returns (uint256) {
        if (oldRate == 0) return 0;
        uint256 diff = oldRate > newRate ? oldRate - newRate : newRate - oldRate;
        return (diff * 10000) / oldRate;
    }

    function _trimHistory(CurrencyPair storage pair) internal {
        uint256 cutoff = block.timestamp - pair.twapPeriod;
        uint256 i = 0;
        
        // Find first timestamp within period
        while (i < pair.timestamps.length && pair.timestamps[i] < cutoff) {
            i++;
        }
        
        if (i > 0 && pair.rateHistory.length > 100) {
            // Shift arrays (gas expensive, only do when necessary)
            uint256 newLength = pair.rateHistory.length - i;
            for (uint256 j = 0; j < newLength; j++) {
                pair.rateHistory[j] = pair.rateHistory[j + i];
                pair.timestamps[j] = pair.timestamps[j + i];
            }
            for (uint256 j = 0; j < i; j++) {
                pair.rateHistory.pop();
                pair.timestamps.pop();
            }
        }
    }

    // ═══════════════════════════════════════════════════════════════════════════
    //                         RATE QUERIES
    // ═══════════════════════════════════════════════════════════════════════════

    /**
     * @notice Get current rate for a currency pair
     */
    function getRate(bytes32 pairId) external view returns (RateResponse memory) {
        return _getRate(pairId);
    }

    /**
     * @notice Get rate for base/quote strings
     */
    function getRateBySymbols(
        string calldata base,
        string calldata quote
    ) external view returns (RateResponse memory) {
        bytes32 pairId = keccak256(abi.encodePacked(base, "/", quote));
        return _getRate(pairId);
    }

    function _getRate(bytes32 pairId) internal view returns (RateResponse memory) {
        CurrencyPair storage pair = pairs[pairId];
        
        bool isStale = block.timestamp - pair.lastUpdate > stalenessThreshold;
        uint256 twapRate = _calculateTWAP(pair);
        
        // Calculate confidence based on oracle agreement
        uint256 confidence = _calculateConfidence(pairId);
        
        return RateResponse({
            spotRate: pair.currentRate,
            twapRate: twapRate,
            confidence: confidence,
            timestamp: pair.lastUpdate,
            isStale: isStale,
            circuitBreakerActive: pair.circuitBreakerTripped
        });
    }

    function _calculateTWAP(CurrencyPair storage pair) internal view returns (uint256) {
        if (pair.rateHistory.length == 0) return pair.currentRate;
        if (pair.rateHistory.length == 1) return pair.rateHistory[0];
        
        uint256 cutoff = block.timestamp - pair.twapPeriod;
        uint256 weightedSum = 0;
        uint256 totalTime = 0;
        
        for (uint256 i = 0; i < pair.rateHistory.length; i++) {
            if (pair.timestamps[i] < cutoff) continue;
            
            uint256 timeDelta;
            if (i == pair.rateHistory.length - 1) {
                timeDelta = block.timestamp - pair.timestamps[i];
            } else {
                timeDelta = pair.timestamps[i + 1] - pair.timestamps[i];
            }
            
            weightedSum += pair.rateHistory[i] * timeDelta;
            totalTime += timeDelta;
        }
        
        if (totalTime == 0) return pair.currentRate;
        return weightedSum / totalTime;
    }

    function _calculateConfidence(bytes32 pairId) internal view returns (uint256) {
        uint256 validOracles = 0;
        uint256 totalConfidence = 0;
        
        for (uint256 i = 0; i < activeOracles.length; i++) {
            PriceData storage price = oraclePrices[pairId][activeOracles[i]];
            if (price.isActive && block.timestamp - price.timestamp <= stalenessThreshold) {
                validOracles++;
                totalConfidence += price.confidence;
            }
        }
        
        if (validOracles == 0) return 0;
        return totalConfidence / validOracles;
    }

    /**
     * @notice Convert amount between currencies
     */
    function convert(
        bytes32 pairId,
        uint256 amount,
        bool inverse
    ) external view returns (uint256 convertedAmount, uint256 rateUsed) {
        totalQueries;
        
        CurrencyPair storage pair = pairs[pairId];
        require(pair.isActive, "Pair not active");
        require(!pair.circuitBreakerTripped, "Circuit breaker active");
        
        rateUsed = pair.currentRate;
        
        if (inverse) {
            // Convert from quote to base
            convertedAmount = (amount * 1e18) / rateUsed;
        } else {
            // Convert from base to quote
            convertedAmount = (amount * rateUsed) / 1e18;
        }
        
        return (convertedAmount, rateUsed);
    }

    // ═══════════════════════════════════════════════════════════════════════════
    //                         CIRCUIT BREAKER
    // ═══════════════════════════════════════════════════════════════════════════

    function resetCircuitBreaker(bytes32 pairId) external onlyRole(ORACLE_ADMIN) {
        pairs[pairId].circuitBreakerTripped = false;
        emit CircuitBreakerReset(pairId);
    }

    function setMaxDeviation(bytes32 pairId, uint256 maxDeviation) external onlyRole(ORACLE_ADMIN) {
        pairs[pairId].maxDeviation = maxDeviation;
    }

    // ═══════════════════════════════════════════════════════════════════════════
    //                         VIEW FUNCTIONS
    // ═══════════════════════════════════════════════════════════════════════════

    function getPairInfo(bytes32 pairId) external view returns (
        string memory base,
        string memory quote,
        uint256 currentRate,
        uint256 lastUpdate,
        bool circuitBreakerTripped
    ) {
        CurrencyPair storage pair = pairs[pairId];
        return (
            pair.base,
            pair.quote,
            pair.currentRate,
            pair.lastUpdate,
            pair.circuitBreakerTripped
        );
    }

    function getAllPairs() external view returns (bytes32[] memory) {
        return activePairs;
    }

    function getOracleStats() external view returns (
        uint256 updates,
        uint256 queries,
        uint256 circuitBreakers,
        uint256 oracleCount
    ) {
        return (totalPriceUpdates, totalQueries, circuitBreakerTriggers, activeOracles.length);
    }

    // ═══════════════════════════════════════════════════════════════════════════
    //                         ADMIN
    // ═══════════════════════════════════════════════════════════════════════════

    function setStalenessThreshold(uint256 threshold) external onlyRole(ORACLE_ADMIN) {
        stalenessThreshold = threshold;
    }

    function setMinOracleCount(uint256 count) external onlyRole(ORACLE_ADMIN) {
        minOracleCount = count;
    }

    function setTwapPeriod(bytes32 pairId, uint256 period) external onlyRole(ORACLE_ADMIN) {
        pairs[pairId].twapPeriod = period;
    }
}

contracts/PayFlowCore.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

import "@openzeppelin/contracts/access/AccessControl.sol";
import "@openzeppelin/contracts/utils/ReentrancyGuard.sol";
import "@openzeppelin/contracts/utils/Pausable.sol";
import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";

/**
 * ╔═══════════════════════════════════════════════════════════════════════════════╗
 * ║                         PAYFLOW PROTOCOL                                       ║
 * ║              Programmable Money Rails for Institutional Payments              ║
 * ╠═══════════════════════════════════════════════════════════════════════════════╣
 * ║  "What if money could carry its own rules?"                                   ║
 * ║                                                                               ║
 * ║  PayFlow transforms institutional payments by embedding programmable          ║
 * ║  conditions directly into payment flows. Every transfer can carry:            ║
 * ║  • Compliance requirements (KYC/AML/Sanctions)                                ║
 * ║  • Time restrictions (business hours, settlement windows)                     ║
 * ║  • Multi-signature approval thresholds                                        ║
 * ║  • Automatic FX conversion at oracle-verified rates                           ║
 * ║  • Escrow conditions with automatic release                                   ║
 * ║  • Immutable audit trails for regulatory compliance                           ║
 * ╚═══════════════════════════════════════════════════════════════════════════════╝
 */

/**
 * @title PayFlowCore
 * @notice The central routing engine for programmable institutional payments
 * @dev Handles payment creation, condition verification, and execution
 */
contract PayFlowCore is AccessControl, ReentrancyGuard, Pausable {
    using SafeERC20 for IERC20;

    // ═══════════════════════════════════════════════════════════════════════════
    //                              ROLES
    // ═══════════════════════════════════════════════════════════════════════════
    
    bytes32 public constant OPERATOR_ROLE = keccak256("OPERATOR_ROLE");
    bytes32 public constant COMPLIANCE_ROLE = keccak256("COMPLIANCE_ROLE");
    bytes32 public constant TREASURY_ROLE = keccak256("TREASURY_ROLE");

    // ═══════════════════════════════════════════════════════════════════════════
    //                              TYPES
    // ═══════════════════════════════════════════════════════════════════════════

    enum PaymentStatus {
        CREATED,        // Payment initiated, awaiting conditions
        PENDING,        // Conditions being verified
        APPROVED,       // All conditions met, ready to execute
        EXECUTED,       // Payment completed successfully
        FAILED,         // Payment failed (conditions not met)
        CANCELLED,      // Payment cancelled by sender
        DISPUTED        // Under dispute resolution
    }

    enum ComplianceTier {
        NONE,           // No KYC required (small amounts only)
        BASIC,          // Email + Phone verification
        STANDARD,       // Government ID + Address
        ENHANCED,       // Full KYC + Source of funds
        INSTITUTIONAL   // Corporate KYC + Ultimate beneficial owner
    }

    struct PaymentConditions {
        // Compliance
        ComplianceTier requiredSenderTier;
        ComplianceTier requiredRecipientTier;
        bool requireSanctionsCheck;
        
        // Time restrictions
        uint256 validFrom;          // Earliest execution time
        uint256 validUntil;         // Latest execution time (0 = no expiry)
        bool businessHoursOnly;     // Only execute during business hours
        
        // Amount controls
        uint256 maxSlippage;        // Max FX slippage in basis points
        
        // Multi-sig (0 = no approval required)
        uint256 requiredApprovals;
        address[] approvers;
        
        // Escrow
        bool useEscrow;
        uint256 escrowReleaseTime;  // Auto-release after this time
        bytes32 escrowConditionHash; // Hash of external condition
    }

    struct Payment {
        // Core payment data
        bytes32 paymentId;
        address sender;
        address recipient;
        address token;
        uint256 amount;
        
        // For cross-border
        address targetToken;        // If different from token, triggers FX
        uint256 targetAmount;       // Expected amount after FX (0 = market rate)
        
        // Status
        PaymentStatus status;
        uint256 createdAt;
        uint256 executedAt;
        
        // Conditions
        PaymentConditions conditions;
        
        // Approvals
        uint256 approvalCount;
        mapping(address => bool) hasApproved;
        
        // Metadata
        bytes32 referenceId;        // External reference
        string memo;
    }

    struct PaymentView {
        bytes32 paymentId;
        address sender;
        address recipient;
        address token;
        uint256 amount;
        address targetToken;
        uint256 targetAmount;
        PaymentStatus status;
        uint256 createdAt;
        uint256 executedAt;
        uint256 approvalCount;
        uint256 requiredApprovals;
        bytes32 referenceId;
    }

    // ═══════════════════════════════════════════════════════════════════════════
    //                              STATE
    // ═══════════════════════════════════════════════════════════════════════════

    // Payment storage
    mapping(bytes32 => Payment) private payments;
    bytes32[] public paymentIds;
    
    // User payment tracking
    mapping(address => bytes32[]) public userPaymentsSent;
    mapping(address => bytes32[]) public userPaymentsReceived;
    
    // Supported tokens
    mapping(address => bool) public supportedTokens;
    address[] public tokenList;
    
    // Module addresses
    address public complianceEngine;
    address public oracleAggregator;
    address public escrowVault;
    address public auditRegistry;
    
    // Protocol settings
    uint256 public protocolFeeBps = 10; // 0.1% default fee
    address public feeRecipient;
    uint256 public maxPaymentAmount = 100_000_000 * 1e6; // $100M default max
    
    // Statistics
    uint256 public totalPaymentsCreated;
    uint256 public totalPaymentsExecuted;
    uint256 public totalVolumeProcessed;
    uint256 public averageSettlementTime;

    // ═══════════════════════════════════════════════════════════════════════════
    //                              EVENTS
    // ═══════════════════════════════════════════════════════════════════════════

    event PaymentCreated(
        bytes32 indexed paymentId,
        address indexed sender,
        address indexed recipient,
        address token,
        uint256 amount,
        bytes32 referenceId
    );
    
    event PaymentApproved(
        bytes32 indexed paymentId,
        address indexed approver,
        uint256 approvalCount,
        uint256 requiredApprovals
    );
    
    event PaymentExecuted(
        bytes32 indexed paymentId,
        address indexed sender,
        address indexed recipient,
        uint256 amount,
        uint256 settlementTime
    );
    
    event PaymentFailed(
        bytes32 indexed paymentId,
        string reason
    );
    
    event PaymentCancelled(
        bytes32 indexed paymentId,
        address indexed cancelledBy
    );
    
    event CrossBorderSettlement(
        bytes32 indexed paymentId,
        address sourceToken,
        address targetToken,
        uint256 sourceAmount,
        uint256 targetAmount,
        uint256 fxRate
    );
    
    event ComplianceVerified(
        bytes32 indexed paymentId,
        address indexed party,
        ComplianceTier tier,
        bool sanctionsCleared
    );

    // ═══════════════════════════════════════════════════════════════════════════
    //                              CONSTRUCTOR
    // ═══════════════════════════════════════════════════════════════════════════

    constructor() {
        _grantRole(DEFAULT_ADMIN_ROLE, msg.sender);
        _grantRole(OPERATOR_ROLE, msg.sender);
        _grantRole(COMPLIANCE_ROLE, msg.sender);
        _grantRole(TREASURY_ROLE, msg.sender);
        feeRecipient = msg.sender;
    }

    // ═══════════════════════════════════════════════════════════════════════════
    //                         PAYMENT CREATION
    // ═══════════════════════════════════════════════════════════════════════════

    /**
     * @notice Create a new programmable payment
     * @param recipient The payment recipient
     * @param token The token to send
     * @param amount The amount to send
     * @param conditions The programmable conditions for this payment
     * @param referenceId External reference ID for tracking
     * @param memo Human-readable memo
     * @return paymentId The unique payment identifier
     */
    function createPayment(
        address recipient,
        address token,
        uint256 amount,
        PaymentConditions calldata conditions,
        bytes32 referenceId,
        string calldata memo
    ) external nonReentrant whenNotPaused returns (bytes32 paymentId) {
        require(recipient != address(0), "Invalid recipient");
        require(recipient != msg.sender, "Cannot pay yourself");
        require(amount > 0, "Amount must be positive");
        require(amount <= maxPaymentAmount, "Exceeds max payment");
        require(supportedTokens[token], "Token not supported");
        
        // Generate unique payment ID
        paymentId = keccak256(abi.encodePacked(
            msg.sender,
            recipient,
            token,
            amount,
            block.timestamp,
            totalPaymentsCreated
        ));
        
        // Store payment
        Payment storage payment = payments[paymentId];
        payment.paymentId = paymentId;
        payment.sender = msg.sender;
        payment.recipient = recipient;
        payment.token = token;
        payment.amount = amount;
        payment.targetToken = token; // Same token by default
        payment.status = PaymentStatus.CREATED;
        payment.createdAt = block.timestamp;
        payment.conditions = conditions;
        payment.referenceId = referenceId;
        payment.memo = memo;
        
        // Copy approvers array
        for (uint i = 0; i < conditions.approvers.length; i++) {
            payment.conditions.approvers.push(conditions.approvers[i]);
        }
        
        // Transfer tokens to protocol
        IERC20(token).safeTransferFrom(msg.sender, address(this), amount);
        
        // Track
        paymentIds.push(paymentId);
        userPaymentsSent[msg.sender].push(paymentId);
        userPaymentsReceived[recipient].push(paymentId);
        totalPaymentsCreated++;
        
        emit PaymentCreated(paymentId, msg.sender, recipient, token, amount, referenceId);
        
        // Auto-process if no conditions
        if (_canAutoExecute(payment)) {
            _executePayment(paymentId);
        } else {
            payment.status = PaymentStatus.PENDING;
        }
        
        return paymentId;
    }

    /**
     * @notice Create a cross-border payment with FX conversion
     */
    function createCrossBorderPayment(
        address recipient,
        address sourceToken,
        uint256 sourceAmount,
        address targetToken,
        uint256 minTargetAmount,
        PaymentConditions calldata conditions,
        bytes32 referenceId,
        string calldata memo
    ) external nonReentrant whenNotPaused returns (bytes32 paymentId) {
        require(recipient != address(0), "Invalid recipient");
        require(sourceAmount > 0, "Amount must be positive");
        require(supportedTokens[sourceToken], "Source token not supported");
        require(supportedTokens[targetToken], "Target token not supported");
        
        paymentId = keccak256(abi.encodePacked(
            msg.sender,
            recipient,
            sourceToken,
            targetToken,
            sourceAmount,
            block.timestamp,
            totalPaymentsCreated
        ));
        
        Payment storage payment = payments[paymentId];
        payment.paymentId = paymentId;
        payment.sender = msg.sender;
        payment.recipient = recipient;
        payment.token = sourceToken;
        payment.amount = sourceAmount;
        payment.targetToken = targetToken;
        payment.targetAmount = minTargetAmount;
        payment.status = PaymentStatus.PENDING;
        payment.createdAt = block.timestamp;
        payment.conditions = conditions;
        payment.referenceId = referenceId;
        payment.memo = memo;
        
        IERC20(sourceToken).safeTransferFrom(msg.sender, address(this), sourceAmount);
        
        paymentIds.push(paymentId);
        userPaymentsSent[msg.sender].push(paymentId);
        userPaymentsReceived[recipient].push(paymentId);
        totalPaymentsCreated++;
        
        emit PaymentCreated(paymentId, msg.sender, recipient, sourceToken, sourceAmount, referenceId);
        
        return paymentId;
    }

    // ═══════════════════════════════════════════════════════════════════════════
    //                         PAYMENT APPROVAL
    // ═══════════════════════════════════════════════════════════════════════════

    /**
     * @notice Approve a payment (for multi-sig payments)
     */
    function approvePayment(bytes32 paymentId) external nonReentrant {
        Payment storage payment = payments[paymentId];
        require(payment.paymentId == paymentId, "Payment not found");
        require(payment.status == PaymentStatus.PENDING, "Not pending approval");
        require(!payment.hasApproved[msg.sender], "Already approved");
        require(_isApprover(payment, msg.sender), "Not an approver");
        
        payment.hasApproved[msg.sender] = true;
        payment.approvalCount++;
        
        emit PaymentApproved(
            paymentId, 
            msg.sender, 
            payment.approvalCount, 
            payment.conditions.requiredApprovals
        );
        
        // Check if ready to execute
        if (_allConditionsMet(payment)) {
            _executePayment(paymentId);
        }
    }

    // ═══════════════════════════════════════════════════════════════════════════
    //                         PAYMENT EXECUTION
    // ═══════════════════════════════════════════════════════════════════════════

    /**
     * @notice Execute a payment that has met all conditions
     */
    function executePayment(bytes32 paymentId) external nonReentrant {
        Payment storage payment = payments[paymentId];
        require(payment.paymentId == paymentId, "Payment not found");
        require(
            payment.status == PaymentStatus.PENDING || 
            payment.status == PaymentStatus.APPROVED,
            "Cannot execute"
        );
        require(_allConditionsMet(payment), "Conditions not met");
        
        _executePayment(paymentId);
    }

    function _executePayment(bytes32 paymentId) internal {
        Payment storage payment = payments[paymentId];
        
        uint256 amountToSend = payment.amount;
        
        // Deduct protocol fee
        if (protocolFeeBps > 0) {
            uint256 fee = (amountToSend * protocolFeeBps) / 10000;
            amountToSend -= fee;
            IERC20(payment.token).safeTransfer(feeRecipient, fee);
        }
        
        // Handle cross-border FX if needed
        if (payment.targetToken != payment.token) {
            // In production, this would call the oracle and DEX
            // For demo, we simulate 1:1 conversion
            amountToSend = _performFXConversion(
                payment.token,
                payment.targetToken,
                amountToSend,
                payment.targetAmount,
                payment.conditions.maxSlippage
            );
            
            emit CrossBorderSettlement(
                paymentId,
                payment.token,
                payment.targetToken,
                payment.amount,
                amountToSend,
                1e18 // Simulated 1:1 rate
            );
        }
        
        // Transfer to recipient
        if (payment.conditions.useEscrow) {
            // Send to escrow vault
            IERC20(payment.targetToken).safeTransfer(escrowVault, amountToSend);
        } else {
            // Direct transfer
            IERC20(payment.targetToken).safeTransfer(payment.recipient, amountToSend);
        }
        
        // Update state
        payment.status = PaymentStatus.EXECUTED;
        payment.executedAt = block.timestamp;
        
        uint256 settlementTime = payment.executedAt - payment.createdAt;
        totalPaymentsExecuted++;
        totalVolumeProcessed += payment.amount;
        
        // Update average settlement time
        averageSettlementTime = (averageSettlementTime * (totalPaymentsExecuted - 1) + settlementTime) / totalPaymentsExecuted;
        
        // Log to audit registry
        if (auditRegistry != address(0)) {
            IAuditRegistry(auditRegistry).logPaymentExecution(
                paymentId,
                payment.sender,
                payment.recipient,
                payment.amount,
                settlementTime
            );
        }
        
        emit PaymentExecuted(
            paymentId,
            payment.sender,
            payment.recipient,
            amountToSend,
            settlementTime
        );
    }

    function _performFXConversion(
        address, // sourceToken
        address targetToken,
        uint256 sourceAmount,
        uint256, // minTargetAmount
        uint256 // maxSlippage
    ) internal view returns (uint256) {
        // In production: Call oracle for rate, execute swap through DEX
        // For hackathon demo: Simulate 1:1 stablecoin conversion
        
        // Verify we have enough target tokens (would come from liquidity pool)
        uint256 balance = IERC20(targetToken).balanceOf(address(this));
        require(balance >= sourceAmount, "Insufficient liquidity");
        
        return sourceAmount;
    }

    // ═══════════════════════════════════════════════════════════════════════════
    //                         PAYMENT CANCELLATION
    // ═══════════════════════════════════════════════════════════════════════════

    /**
     * @notice Cancel a pending payment
     */
    function cancelPayment(bytes32 paymentId) external nonReentrant {
        Payment storage payment = payments[paymentId];
        require(payment.paymentId == paymentId, "Payment not found");
        require(payment.sender == msg.sender, "Not the sender");
        require(
            payment.status == PaymentStatus.CREATED || 
            payment.status == PaymentStatus.PENDING,
            "Cannot cancel"
        );
        
        // Refund
        IERC20(payment.token).safeTransfer(payment.sender, payment.amount);
        
        payment.status = PaymentStatus.CANCELLED;
        
        emit PaymentCancelled(paymentId, msg.sender);
    }

    // ═══════════════════════════════════════════════════════════════════════════
    //                         CONDITION CHECKING
    // ═══════════════════════════════════════════════════════════════════════════

    function _canAutoExecute(Payment storage payment) internal view returns (bool) {
        PaymentConditions storage c = payment.conditions;
        
        // No conditions = auto execute
        if (
            c.requiredSenderTier == ComplianceTier.NONE &&
            c.requiredRecipientTier == ComplianceTier.NONE &&
            !c.requireSanctionsCheck &&
            c.validFrom == 0 &&
            c.validUntil == 0 &&
            !c.businessHoursOnly &&
            c.requiredApprovals == 0 &&
            !c.useEscrow
        ) {
            return true;
        }
        return false;
    }

    function _allConditionsMet(Payment storage payment) internal view returns (bool) {
        PaymentConditions storage c = payment.conditions;
        
        // Time checks
        if (c.validFrom > 0 && block.timestamp < c.validFrom) return false;
        if (c.validUntil > 0 && block.timestamp > c.validUntil) return false;
        
        // Approval checks
        if (c.requiredApprovals > 0 && payment.approvalCount < c.requiredApprovals) {
            return false;
        }
        
        // In production: Check compliance engine for KYC/AML status
        // For demo: Always passes
        
        return true;
    }

    function _isApprover(Payment storage payment, address account) internal view returns (bool) {
        for (uint i = 0; i < payment.conditions.approvers.length; i++) {
            if (payment.conditions.approvers[i] == account) return true;
        }
        return false;
    }

    // ═══════════════════════════════════════════════════════════════════════════
    //                         VIEW FUNCTIONS
    // ═══════════════════════════════════════════════════════════════════════════

    function getPayment(bytes32 paymentId) external view returns (PaymentView memory) {
        Payment storage payment = payments[paymentId];
        return PaymentView({
            paymentId: payment.paymentId,
            sender: payment.sender,
            recipient: payment.recipient,
            token: payment.token,
            amount: payment.amount,
            targetToken: payment.targetToken,
            targetAmount: payment.targetAmount,
            status: payment.status,
            createdAt: payment.createdAt,
            executedAt: payment.executedAt,
            approvalCount: payment.approvalCount,
            requiredApprovals: payment.conditions.requiredApprovals,
            referenceId: payment.referenceId
        });
    }

    function getPaymentCount() external view returns (uint256) {
        return paymentIds.length;
    }

    function getProtocolStats() external view returns (
        uint256 created,
        uint256 executed,
        uint256 volume,
        uint256 avgSettlement
    ) {
        return (
            totalPaymentsCreated,
            totalPaymentsExecuted,
            totalVolumeProcessed,
            averageSettlementTime
        );
    }

    function getUserPayments(address user) external view returns (
        bytes32[] memory sent,
        bytes32[] memory received
    ) {
        return (userPaymentsSent[user], userPaymentsReceived[user]);
    }

    // ═══════════════════════════════════════════════════════════════════════════
    //                         ADMIN FUNCTIONS
    // ═══════════════════════════════════════════════════════════════════════════

    function addSupportedToken(address token) external onlyRole(OPERATOR_ROLE) {
        require(!supportedTokens[token], "Already supported");
        supportedTokens[token] = true;
        tokenList.push(token);
    }

    function setModules(
        address _compliance,
        address _oracle,
        address _escrow,
        address _audit
    ) external onlyRole(DEFAULT_ADMIN_ROLE) {
        complianceEngine = _compliance;
        oracleAggregator = _oracle;
        escrowVault = _escrow;
        auditRegistry = _audit;
    }

    function setProtocolFee(uint256 _feeBps) external onlyRole(TREASURY_ROLE) {
        require(_feeBps <= 100, "Fee too high"); // Max 1%
        protocolFeeBps = _feeBps;
    }

    function setFeeRecipient(address _recipient) external onlyRole(TREASURY_ROLE) {
        require(_recipient != address(0), "Invalid recipient");
        feeRecipient = _recipient;
    }

    function pause() external onlyRole(OPERATOR_ROLE) {
        _pause();
    }

    function unpause() external onlyRole(OPERATOR_ROLE) {
        _unpause();
    }
}

// ═══════════════════════════════════════════════════════════════════════════
//                         INTERFACES
// ═══════════════════════════════════════════════════════════════════════════

interface IAuditRegistry {
    function logPaymentExecution(
        bytes32 paymentId,
        address sender,
        address recipient,
        uint256 amount,
        uint256 settlementTime
    ) external;
}

contracts/SmartEscrow.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

import "@openzeppelin/contracts/access/AccessControl.sol";
import "@openzeppelin/contracts/utils/ReentrancyGuard.sol";
import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";

/**
 * @title SmartEscrow
 * @notice Programmable escrow with automatic release conditions
 * @dev Supports time-based, oracle-based, and multi-sig release conditions
 * 
 * USE CASES:
 * - Cross-border trade finance (release on delivery confirmation)
 * - Milestone-based payments (release on approval)
 * - Dispute-protected transactions (automatic release after period)
 */
contract SmartEscrow is AccessControl, ReentrancyGuard {
    using SafeERC20 for IERC20;

    bytes32 public constant OPERATOR_ROLE = keccak256("OPERATOR_ROLE");
    bytes32 public constant ARBITER_ROLE = keccak256("ARBITER_ROLE");

    enum EscrowStatus {
        ACTIVE,         // Funds locked, awaiting conditions
        RELEASED,       // Funds released to recipient
        REFUNDED,       // Funds returned to sender
        DISPUTED        // Under dispute resolution
    }

    enum ReleaseCondition {
        TIME_BASED,     // Auto-release after timestamp
        APPROVAL,       // Manual approval by parties
        ORACLE,         // External oracle confirmation
        MULTI_SIG       // Multiple signatures required
    }

    struct Escrow {
        bytes32 escrowId;
        bytes32 paymentId;      // Link to PayFlowCore payment
        
        address depositor;
        address beneficiary;
        address arbiter;        // Dispute resolver
        
        address token;
        uint256 amount;
        uint256 fee;
        
        EscrowStatus status;
        ReleaseCondition conditionType;
        
        // Time conditions
        uint256 releaseTime;    // Auto-release timestamp
        uint256 disputeDeadline; // Deadline to raise dispute
        
        // Approval tracking
        bool depositorApproved;
        bool beneficiaryApproved;
        
        // Multi-sig
        uint256 requiredSignatures;
        uint256 signatureCount;
        mapping(address => bool) hasSigned;
        
        // Oracle condition
        bytes32 oracleConditionId;
        bool oracleConditionMet;
        
        // Metadata
        uint256 createdAt;
        string description;
    }

    struct EscrowView {
        bytes32 escrowId;
        bytes32 paymentId;
        address depositor;
        address beneficiary;
        address token;
        uint256 amount;
        EscrowStatus status;
        ReleaseCondition conditionType;
        uint256 releaseTime;
        bool depositorApproved;
        bool beneficiaryApproved;
        uint256 createdAt;
    }

    // State
    mapping(bytes32 => Escrow) private escrows;
    bytes32[] public escrowIds;
    
    mapping(address => bytes32[]) public userEscrowsAsDepositor;
    mapping(address => bytes32[]) public userEscrowsAsBeneficiary;
    
    uint256 public escrowFeeBps = 5; // 0.05%
    address public feeRecipient;
    address public payFlowCore;
    
    // Statistics
    uint256 public totalEscrowsCreated;
    uint256 public totalValueLocked;
    uint256 public totalReleased;

    // Events
    event EscrowCreated(
        bytes32 indexed escrowId,
        bytes32 indexed paymentId,
        address depositor,
        address beneficiary,
        uint256 amount
    );
    
    event EscrowApproved(
        bytes32 indexed escrowId,
        address indexed approver
    );
    
    event EscrowReleased(
        bytes32 indexed escrowId,
        address indexed beneficiary,
        uint256 amount
    );
    
    event EscrowRefunded(
        bytes32 indexed escrowId,
        address indexed depositor,
        uint256 amount
    );
    
    event EscrowDisputed(
        bytes32 indexed escrowId,
        address indexed disputedBy,
        string reason
    );
    
    event DisputeResolved(
        bytes32 indexed escrowId,
        address indexed resolvedBy,
        address recipient,
        uint256 amount
    );

    constructor() {
        _grantRole(DEFAULT_ADMIN_ROLE, msg.sender);
        _grantRole(OPERATOR_ROLE, msg.sender);
        _grantRole(ARBITER_ROLE, msg.sender);
        feeRecipient = msg.sender;
    }

    // ═══════════════════════════════════════════════════════════════════════════
    //                         ESCROW CREATION
    // ═══════════════════════════════════════════════════════════════════════════

    /**
     * @notice Create a new time-based escrow
     */
    function createTimeBasedEscrow(
        bytes32 paymentId,
        address beneficiary,
        address token,
        uint256 amount,
        uint256 releaseTime,
        uint256 disputeWindow,
        string calldata description
    ) external nonReentrant returns (bytes32 escrowId) {
        require(beneficiary != address(0), "Invalid beneficiary");
        require(amount > 0, "Amount must be positive");
        require(releaseTime > block.timestamp, "Release time must be future");
        
        escrowId = _createEscrow(
            paymentId,
            msg.sender,
            beneficiary,
            token,
            amount,
            ReleaseCondition.TIME_BASED,
            releaseTime,
            disputeWindow,
            description
        );
        
        return escrowId;
    }

    /**
     * @notice Create an approval-based escrow
     */
    function createApprovalEscrow(
        bytes32 paymentId,
        address beneficiary,
        address token,
        uint256 amount,
        uint256 disputeDeadline,
        string calldata description
    ) external nonReentrant returns (bytes32 escrowId) {
        require(beneficiary != address(0), "Invalid beneficiary");
        require(amount > 0, "Amount must be positive");
        
        escrowId = _createEscrow(
            paymentId,
            msg.sender,
            beneficiary,
            token,
            amount,
            ReleaseCondition.APPROVAL,
            0, // No auto-release
            disputeDeadline,
            description
        );
        
        return escrowId;
    }

    function _createEscrow(
        bytes32 paymentId,
        address depositor,
        address beneficiary,
        address token,
        uint256 amount,
        ReleaseCondition conditionType,
        uint256 releaseTime,
        uint256 disputeWindow,
        string calldata description
    ) internal returns (bytes32 escrowId) {
        escrowId = keccak256(abi.encodePacked(
            depositor,
            beneficiary,
            token,
            amount,
            block.timestamp,
            totalEscrowsCreated
        ));
        
        // Calculate fee
        uint256 fee = (amount * escrowFeeBps) / 10000;
        
        // Transfer tokens
        IERC20(token).safeTransferFrom(depositor, address(this), amount);
        
        // Store escrow
        Escrow storage escrow = escrows[escrowId];
        escrow.escrowId = escrowId;
        escrow.paymentId = paymentId;
        escrow.depositor = depositor;
        escrow.beneficiary = beneficiary;
        escrow.arbiter = msg.sender; // Default arbiter, can be changed
        escrow.token = token;
        escrow.amount = amount - fee;
        escrow.fee = fee;
        escrow.status = EscrowStatus.ACTIVE;
        escrow.conditionType = conditionType;
        escrow.releaseTime = releaseTime;
        escrow.disputeDeadline = block.timestamp + disputeWindow;
        escrow.createdAt = block.timestamp;
        escrow.description = description;
        
        // Track
        escrowIds.push(escrowId);
        userEscrowsAsDepositor[depositor].push(escrowId);
        userEscrowsAsBeneficiary[beneficiary].push(escrowId);
        totalEscrowsCreated++;
        totalValueLocked += escrow.amount;
        
        // Send fee
        if (fee > 0) {
            IERC20(token).safeTransfer(feeRecipient, fee);
        }
        
        emit EscrowCreated(escrowId, paymentId, depositor, beneficiary, escrow.amount);
        
        return escrowId;
    }

    // ═══════════════════════════════════════════════════════════════════════════
    //                         ESCROW APPROVAL & RELEASE
    // ═══════════════════════════════════════════════════════════════════════════

    /**
     * @notice Approve escrow release (for approval-based escrows)
     */
    function approveRelease(bytes32 escrowId) external nonReentrant {
        Escrow storage escrow = escrows[escrowId];
        require(escrow.escrowId == escrowId, "Escrow not found");
        require(escrow.status == EscrowStatus.ACTIVE, "Not active");
        
        if (msg.sender == escrow.depositor) {
            require(!escrow.depositorApproved, "Already approved");
            escrow.depositorApproved = true;
        } else if (msg.sender == escrow.beneficiary) {
            require(!escrow.beneficiaryApproved, "Already approved");
            escrow.beneficiaryApproved = true;
        } else {
            revert("Not a party to this escrow");
        }
        
        emit EscrowApproved(escrowId, msg.sender);
        
        // Check if both approved
        if (escrow.depositorApproved && escrow.conditionType == ReleaseCondition.APPROVAL) {
            _releaseEscrow(escrowId);
        }
    }

    /**
     * @notice Release escrow to beneficiary
     */
    function releaseEscrow(bytes32 escrowId) external nonReentrant {
        Escrow storage escrow = escrows[escrowId];
        require(escrow.escrowId == escrowId, "Escrow not found");
        require(escrow.status == EscrowStatus.ACTIVE, "Not active");
        require(_canRelease(escrow), "Conditions not met");
        
        _releaseEscrow(escrowId);
    }

    function _releaseEscrow(bytes32 escrowId) internal {
        Escrow storage escrow = escrows[escrowId];
        
        escrow.status = EscrowStatus.RELEASED;
        totalValueLocked -= escrow.amount;
        totalReleased += escrow.amount;
        
        IERC20(escrow.token).safeTransfer(escrow.beneficiary, escrow.amount);
        
        emit EscrowReleased(escrowId, escrow.beneficiary, escrow.amount);
    }

    function _canRelease(Escrow storage escrow) internal view returns (bool) {
        if (escrow.conditionType == ReleaseCondition.TIME_BASED) {
            return block.timestamp >= escrow.releaseTime;
        }
        
        if (escrow.conditionType == ReleaseCondition.APPROVAL) {
            return escrow.depositorApproved;
        }
        
        if (escrow.conditionType == ReleaseCondition.ORACLE) {
            return escrow.oracleConditionMet;
        }
        
        if (escrow.conditionType == ReleaseCondition.MULTI_SIG) {
            return escrow.signatureCount >= escrow.requiredSignatures;
        }
        
        return false;
    }

    // ═══════════════════════════════════════════════════════════════════════════
    //                         DISPUTE HANDLING
    // ═══════════════════════════════════════════════════════════════════════════

    /**
     * @notice Raise a dispute on an escrow
     */
    function raiseDispute(bytes32 escrowId, string calldata reason) external {
        Escrow storage escrow = escrows[escrowId];
        require(escrow.escrowId == escrowId, "Escrow not found");
        require(escrow.status == EscrowStatus.ACTIVE, "Not active");
        require(
            msg.sender == escrow.depositor || msg.sender == escrow.beneficiary,
            "Not a party"
        );
        require(block.timestamp <= escrow.disputeDeadline, "Dispute window closed");
        
        escrow.status = EscrowStatus.DISPUTED;
        
        emit EscrowDisputed(escrowId, msg.sender, reason);
    }

    /**
     * @notice Resolve a dispute (arbiter only)
     */
    function resolveDispute(
        bytes32 escrowId,
        address recipient,
        uint256 amount
    ) external onlyRole(ARBITER_ROLE) {
        Escrow storage escrow = escrows[escrowId];
        require(escrow.escrowId == escrowId, "Escrow not found");
        require(escrow.status == EscrowStatus.DISPUTED, "Not disputed");
        require(
            recipient == escrow.depositor || recipient == escrow.beneficiary,
            "Invalid recipient"
        );
        require(amount <= escrow.amount, "Amount too high");
        
        // Transfer decided amount to recipient
        if (amount > 0) {
            IERC20(escrow.token).safeTransfer(recipient, amount);
        }
        
        // Refund remainder to other party
        uint256 remainder = escrow.amount - amount;
        if (remainder > 0) {
            address other = recipient == escrow.beneficiary ? escrow.depositor : escrow.beneficiary;
            IERC20(escrow.token).safeTransfer(other, remainder);
        }
        
        escrow.status = EscrowStatus.RELEASED;
        totalValueLocked -= escrow.amount;
        
        emit DisputeResolved(escrowId, msg.sender, recipient, amount);
    }

    /**
     * @notice Request refund (depositor only, before release)
     */
    function requestRefund(bytes32 escrowId) external nonReentrant {
        Escrow storage escrow = escrows[escrowId];
        require(escrow.escrowId == escrowId, "Escrow not found");
        require(msg.sender == escrow.depositor, "Not depositor");
        require(escrow.status == EscrowStatus.ACTIVE, "Not active");
        
        // For approval escrows, beneficiary must agree
        if (escrow.conditionType == ReleaseCondition.APPROVAL) {
            require(escrow.beneficiaryApproved, "Beneficiary must agree to refund");
        }
        
        escrow.status = EscrowStatus.REFUNDED;
        totalValueLocked -= escrow.amount;
        
        IERC20(escrow.token).safeTransfer(escrow.depositor, escrow.amount);
        
        emit EscrowRefunded(escrowId, escrow.depositor, escrow.amount);
    }

    // ═══════════════════════════════════════════════════════════════════════════
    //                         VIEW FUNCTIONS
    // ═══════════════════════════════════════════════════════════════════════════

    function getEscrow(bytes32 escrowId) external view returns (EscrowView memory) {
        Escrow storage escrow = escrows[escrowId];
        return EscrowView({
            escrowId: escrow.escrowId,
            paymentId: escrow.paymentId,
            depositor: escrow.depositor,
            beneficiary: escrow.beneficiary,
            token: escrow.token,
            amount: escrow.amount,
            status: escrow.status,
            conditionType: escrow.conditionType,
            releaseTime: escrow.releaseTime,
            depositorApproved: escrow.depositorApproved,
            beneficiaryApproved: escrow.beneficiaryApproved,
            createdAt: escrow.createdAt
        });
    }

    function getProtocolStats() external view returns (
        uint256 created,
        uint256 locked,
        uint256 released
    ) {
        return (totalEscrowsCreated, totalValueLocked, totalReleased);
    }

    // ═══════════════════════════════════════════════════════════════════════════
    //                         ADMIN
    // ═══════════════════════════════════════════════════════════════════════════

    function setPayFlowCore(address _core) external onlyRole(DEFAULT_ADMIN_ROLE) {
        payFlowCore = _core;
    }

    function setFeeRecipient(address _recipient) external onlyRole(DEFAULT_ADMIN_ROLE) {
        feeRecipient = _recipient;
    }

    function setEscrowFee(uint256 _feeBps) external onlyRole(DEFAULT_ADMIN_ROLE) {
        require(_feeBps <= 50, "Max 0.5%");
        escrowFeeBps = _feeBps;
    }

    /**
     * @notice Oracle callback to mark condition as met
     */
    function setOracleConditionMet(bytes32 escrowId) external onlyRole(OPERATOR_ROLE) {
        Escrow storage escrow = escrows[escrowId];
        require(escrow.conditionType == ReleaseCondition.ORACLE, "Not oracle escrow");
        escrow.oracleConditionMet = true;
    }
}

